#!/bin/bash
cd /etc/kubernetes/ && /etc/kubernetes/bin/kubectl config set-cluster kubernetes --certificate-authority=/etc/kubernetes/cert/ca.pem --embed-certs=true --server=https://127.0.0.1:8443 --kubeconfig=kube-controller-manager.kubeconfig && /etc/kubernetes/bin/kubectl config set-credentials system:kube-controller-manager --client-certificate=/etc/kubernetes/cert/kube-controller-manager.pem --client-key=/etc/kubernetes/cert/kube-controller-manager-key.pem --embed-certs=true --kubeconfig=kube-controller-manager.kubeconfig && /etc/kubernetes/bin/kubectl config set-context system:kube-controller-manager --cluster=kubernetes --user=system:kube-controller-manager --kubeconfig=kube-controller-manager.kubeconfig && /etc/kubernetes/bin/kubectl config use-context system:kube-controller-manager --kubeconfig=kube-controller-manager.kubeconfig
