#!/bin/bash
cd /etc/kubernetes/ && /etc/kubernetes/bin/kubectl config set-cluster kubernetes --certificate-authority=/etc/kubernetes/cert/ca.pem --embed-certs=true --server=https://127.0.0.1:8443 --kubeconfig=kube-scheduler.kubeconfig && /etc/kubernetes/bin/kubectl config set-credentials system:kube-scheduler --client-certificate=/etc/kubernetes/cert/kube-scheduler.pem --client-key=/etc/kubernetes/cert/kube-scheduler-key.pem --embed-certs=true --kubeconfig=kube-scheduler.kubeconfig && /etc/kubernetes/bin/kubectl config set-context system:kube-scheduler --cluster=kubernetes --user=system:kube-scheduler --kubeconfig=kube-scheduler.kubeconfig && /etc/kubernetes/bin/kubectl config use-context system:kube-scheduler --kubeconfig=kube-scheduler.kubeconfig
